import React, {useEffect, useState, memo, useRef} from 'react'
import './SlidePictures.scss'

const SlidePictures = memo((props) => {
    const CONTROL_INDEX = 260;          // 控制图片的切换速度
    // 复制第一张图片在最后
    const picturesArr = [...props.picturesArr, props.picturesArr[0]];
    const [mouseEnter, setMouseEnter] = useState(false);       //鼠标是否悬浮在整个区域上
    const [pictureShowing, setPictureShowing] = useState(0);   //当前展示的是第几张图片
    const [hasClicked, setHasClicked] = useState(false);       //函数节流，防止用户快速、频繁点击图片变换导致出错，flase为图片变换操作不可执行

    const picturesUl = useRef();                    // 图片外部的ul
    const circlesContainer = useRef();              // 底部的小圆圈
    const customChanging = useRef(false);           // 是否人为地引起了改变

    const observer = new IntersectionObserver((entries, observer) => {  
        entries.forEach(entry => {  
            if(entry.isIntersecting) {  
                const lazyImage = entry.target;  
                lazyImage.src = lazyImage.dataset.src;
                observer.unobserve(lazyImage);  
            }  
        });  
    });

    // 控制图片默认切换的方法(当前所展示的图片索引，用于控制是否变换的值)
    const changeAction = (currentPictureIndex, controlIndex, currentIndex) =>{
        // console.log(currentPictureIndex, controlIndex, currentIndex)
        if(currentIndex < controlIndex){
            requestAnimationFrame(() => {changeAction(currentPictureIndex, controlIndex, currentIndex + 1)})
        }else{
            // 如果有 5 张有效的图片，那么length = 6
            const length = picturesArr.length;
            const nextIndex = (currentPictureIndex + 1) % length;
            // pictureShowing 的范围为 0 ~ 5
            setPictureShowing(nextIndex);
            if(nextIndex >= 5){
                requestAnimationFrame(() => {fromEndPicToFirst(nextIndex, controlIndex, 1)})
            }else{
                requestAnimationFrame(() => {changeAction(nextIndex, controlIndex, 1)})
            }
        }
    }

    // 从最后一张图片切换到第一张图片的一个小动作
    const fromEndPicToFirst = (currentPictureIndex, controlIndex, currentIndex) =>{
        if(currentIndex === (controlIndex - 40)){
            // 移除 transition 类
            picturesUl.current.classList.remove('ul-transition');
            // ul 的 translateX 会变为 0 
            setPictureShowing(0);
            requestAnimationFrame(() => {fromEndPicToFirst(currentPictureIndex, controlIndex, currentIndex + 1)})
        }else if(currentIndex >= controlIndex - 30){
            picturesUl.current.classList.add('ul-transition');
            requestAnimationFrame(() => {changeAction(0, controlIndex, currentIndex + 1)})
        }else{
            requestAnimationFrame(() => {fromEndPicToFirst(currentPictureIndex, controlIndex, currentIndex + 1)})
        }
    }

    // 上一张
    const lastPicutre = () => {
        if(customChanging.current) return;
        else{
            customChanging.current = true;
            requestAnimationFrame(()=>changeAction(pictureShowing - 1, CONTROL_INDEX, CONTROL_INDEX - 2))
        }
    }
    // 
    const nextPictures = () => {
        if(customChanging.current) return;
        else{
            customChanging.current = true;
            requestAnimationFrame(()=>changeAction(pictureShowing - 1, CONTROL_INDEX, CONTROL_INDEX - 2))
        }
    }
    const gotoDirectly = (index) => {

    }

    useEffect(()=>{
        // 如果有 5 张有效的图片，那么有效索引 realIndex 在 0~4, 但是 pictureShowing 可以取到 5
        const realIndex = pictureShowing >= 5 ? 0 : pictureShowing
        // 改变底部小圆圈的样式
        let circlesArr = Array.from(circlesContainer.current.childNodes);
        circlesArr.forEach(circle => { circle.style.background = ""; })
        circlesArr[realIndex].style.background = "black";
    },[pictureShowing])

    useEffect(()=>{
        // 配置图片懒加载
        const lazyImages = Array.from(picturesUl.current.childNodes).map(e => e.childNodes[0])
        if('IntersectionObserver' in window){  
            lazyImages.forEach(image => { observer.observe(image); });  
        }else{
            lazyImages.forEach(image => { image.src = image.dataset.src; });  
        }
        // 启动轮播图
        requestAnimationFrame(() => {changeAction(0, CONTROL_INDEX, 1)})
    },[])   // 注意，如果没有第二个参数（空数组），那么每次更新都会执行这个副作用。空数组保证只执行一次

    return (
        <div className="outest" onMouseOver={() => {setMouseEnter(true)}} onMouseLeave={() => {setMouseEnter(false)}} >
            {/* 向左icon，鼠标进入轮播图区域时才显现 */}
            {
                mouseEnter ? <img className="icon _left" src="left.png" 
                    alt="last" onClick={lastPicutre}/> : ''
            }

            {/* 主要图片 */}
            <ul ref={picturesUl} className='ul-transition' style={{
                transform: `translate(calc(${pictureShowing} * -600px), 0)`
            }}>
            {
                picturesArr.map((e, i) => {
                    return (<li key={i}>
                        <img data-src={e.src} alt={e.alt} loading='lazy'/>
                    </li>)
                })
            }
            </ul>

            {/* 底部小圆圈的设置 */}
            <div className="mydiv" ref={circlesContainer}>
                {
                    props.picturesArr.map(e =>{
                        return (<div
                            className="empty-cicle" key={e.index} onClick={gotoDirectly(e.index)}>
                            </div>)
                    })
                }
            </div>

            {/* 向右icon，鼠标进入轮播图区域时才显现 */}
            {
                mouseEnter ? <img className="icon _right" src="right.png" alt="next" 
                    onClick={nextPictures}/> : ''
            }
        </div>
    )
})

export default SlidePictures;
